      if (index.length === 0) {
        return 0;
      }

      var minVal = rcd[index[min]][prop];
      var maxVal = rcd[index[max]][prop];

      // hone in on start position of value
      while (min < max) {
        mid = (min + max) >> 1;

        if (ltHelper(rcd[index[mid]][prop], val, false)) {
          min = mid + 1;
        } else {
          max = mid;
        }
      }

      var lbound = min;

      if (ltHelper(rcd[index[lbound]][prop], val, false)) {
        return lbound+1;
      }
      else {
        return lbound;
      }
