      var max = index.length - 1;
      var mid = 0;

      if (index.length === 0) {
        return 0;
      }

      var minVal = rcd[index[min]][prop];
      var maxVal = rcd[index[max]][prop];

      // hone in on start position of value
      while (min < max) {
        mid = (min + max) >> 1;

        if (ltHelper(val, rcd[index[mid]][prop], false)) {
          max = mid;
        } else {
          min = mid + 1;
        }
      }

      var ubound = max;

      if (gtHelper(rcd[index[ubound]][prop], val, false)) {
        return ubound-1;
      }
      else {
        return ubound;
      }
